            console.log('This should be []:', array);
        });

        function soon(val) {
            return new Promise(resolve =&gt; {
                setTimeout(() =&gt; resolve(val), Math.random() * 500);
            });
        }

        Promise_all([soon(1), soon(2), soon(3)]).then(array =&gt; {
            console.log('This should be [1, 2, 3]:', array);
        });

