&lt;!DOCTYPE html&gt;

&lt;div id="progress"&gt;&lt;/div&gt;

&lt;script&gt;
'use strict';

let start = Date.now();

let i = 0;

let chunk = () =&gt; {
  // do a piece of the heavy job (*)
  do {
    i++;
  } while (i % 1e5 != 0);
  progress.innerHTML = i;
};

let stop = () =&gt; (i == 1e7);

function count(task, condition) { 
  if (condition()) {
    alert("Done in " + (Date.now() - start) + 'ms');
  } else {
    setTimeout(() =&gt; count(task, condition)); // schedule the new call (**)
  };
  task();
}

count(chunk, stop);
&lt;/script&gt;


